    def validar_campovacio(self):
        entrada = self.label_entrada.text()
        if entrada == "":
            msg = QtWidgets.QMessageBox()
            msg.setIcon(QtWidgets.QMessageBox.Warning)
            msg.setText("Campos Vacios")
